mapper.update:
update board
set title=#{title},content=#{content}
where bno=#{bno}

mapper.select:
select bno,title,b.regdate,content,viewcnt,u_name,writer
 from board b,t_user u
 where b.writer=u.u_id and bno=#{bno}

choose.when:
 		where b.writer=u.u_id 
		and show='Y'
		and ${search_option} like #{keyword}

choose.otherwise:
 	where b.writer=u.u_id 
and show='Y'
and (writer like #{keyword}
 or title like #{keyword}
 or content like #{keyword} )

